class HandlerCommand {
    #name;
    #func;
    #args;
    #flags;
    #opts;
    
    constructor(name, args, func) {
        this.#name = name;
        this.#func = func;
        this.#args = [];
        this.#flags = [];
        this.#opts = args.opts;
        
        //Parse the args
        if (Array.isArray(args.args)) {
            this.#args = args.args;
        }
        
        //Parse the flags
        if (Array.isArray(args.flags)) {
            this.#flags = args.flags;
        }
    }
    
    get name() {
        return this.#name;
    }
    
    get func() {
        return this.#func;
    }
    
    get args() {
        return this.#args;
    }
    
    get flags() {
        return this.#flags;
    }
    
    get opts() {
        return this.#opts;
    }
}

module.exports = HandlerCommand;