const moment = require("moment");

module.exports = function(text, severity = 1) {
    let colour;
    switch (severity) {
        case 2:
            colour = "\x1b[32m";
            break;
        case 3:
            colour = "\x1b[33m";
            break;
        case 4:
            colour = "\x1b[31m";
            break;
        default:
            colour = "";
    }

    let time = moment().format("kk:mm:ss");

    console.log(`${colour}[${time}] ${text}\x1b[0m`);
}

module.exports.debug = 0;
module.exports.info = 1
module.exports.success = 2;
module.exports.warn = 3;
module.exports.error = 4;
